from conan import ConanFile
from conans import tools
from conan.tools.cmake import CMake, CMakeToolchain, CMakeDeps

class CesiumUtilityConan(ConanFile):
    name = "cesium3dtilesselection"
    version = "0.12.0"
    user = "user"
    channel = "dev"
    license = "Apache-2.0"
    author = "CesiumGS, Inc. and Contributors"
    url = "https://github.com/CesiumGS/cesium-native"
    description = "Runtime streaming, decoding, level of detail selection, culling, cache management, and decoding of 3D Tiles."
    topics = () # TODO
    settings = "os", "compiler", "build_type", "arch"
    options = {"shared": [True, False], "fPIC": [True, False]}
    default_options = {"shared": False, "fPIC": True}
    requires = [
      "spdlog/1.9.2",
      "tinyxml2/9.0.0",
      "ms-gsl/4.0.0",
      "uriparser/0.9.6",
      "rapidjson/cci.20211112",
      "cesiumasync/0.12.0@user/dev",
      "cesiumgeospatial/0.12.0@user/dev",
      "cesiumgeometry/0.12.0@user/dev",
      "cesiumgltf/0.12.0@user/dev",
      "cesiumgltfreader/0.12.0@user/dev",
      "cesiumutility/0.12.0@user/dev",
    ]

    exports_sources = [
      "include/*",
      "generated/*",
      "src/*",
      "test/*",
      "CMakeLists.txt",
      "../tools/cmake/cesium.cmake"
    ]

    def config_options(self):
      if self.settings.os == "Windows":
          del self.options.fPIC

    def generate(self):
      tc = CMakeToolchain(self)
      tc.variables["CESIUM_USE_CONAN_PACKAGES"] = True
      tc.variables["CESIUM_TESTS_ENABLED"] = False
      tc.generate()

      deps = CMakeDeps(self)
      deps.generate()

    def build(self):
      cmake = CMake(self)
      cmake.configure()
      cmake.build()
      # if self.develop:
      #   cmake.test()

    def package(self):
      cmake = CMake(self)
      cmake.install()

    def package_info(self):
      self.cpp_info.libs = tools.collect_libs(self)
      self.cpp_info.set_property("cmake_file_name", "Cesium3DTilesSelection")
      self.cpp_info.set_property("cmake_target_name", "Cesium3DTilesSelection::Cesium3DTilesSelection")